import java.util.ArrayList; 


ArrayList messages = new ArrayList();


String inputVal = (String)value; 

if (inputVal.indexOf("@") < 0) { 
    messages.add("Need an @ sign in a valid email.");  
} 


if (inputVal.indexOf(".") < 0) { 
    messages.add("Need an . in a valid email.");  
} 

return messages;
