import java.util.List;
print("Beanshell namespace:");
    for (int i = 0 ; i &lt; this.variables.length() ; i++)  {
       String name = this.variables[i];
       Object value = eval(name);
       if (value == void)
           print(name + " = void");
       else if (value == null)
           print(name + " = null");
       else
           print(name + ": " + value.getClass().getSimpleName() + " = " + value);
    }
